import React from 'react';
import { useSelector } from 'react-redux';

export default function NotificationPanel() {
  const notifications = useSelector(s => s.ui.notifications || []);
  return (
    <div className="notification-panel">
      <h4>Notifications</h4>
      <div className="list">
        {notifications.length === 0 && <div className="muted">No notifications</div>}
        {notifications.map(n => (
          <div key={n._id || n.createdAt} className={`notif-item ${n.read ? 'read' : 'unread'}`}>
            <div className="title">{n.title}</div>
            <div className="body">{n.body}</div>
            <div className="time">{new Date(n.createdAt).toLocaleString()}</div>
          </div>
        ))}
      </div>
    </div>
  );
}
