import { configureStore } from '@reduxjs/toolkit';
import authReducer from '../features/auth/authSlice';

import uiReducer from '../features/ui/uiSlice';

export default configureStore({
  reducer: {
    auth: authReducer,
    chat: chatReducer,
    ui: uiReducer
  }
});
