import React from 'react';

export default function NotificationPanel() {
  const notifications = [
    { title: 'Welcome!', body: 'Thanks for joining MyChat ðŸŽ‰', createdAt: new Date(), read: false },
    { title: 'Reminder', body: 'Donâ€™t forget your meeting at 5 PM.', createdAt: new Date(), read: true }
  ];

  return (
    <div className="notification-panel">
      <h4>Notifications</h4>
      <div className="list">
        {notifications.length === 0 && <div className="muted">No notifications</div>}
        {notifications.map((n, i) => (
          <div key={i} className={`notif-item ${n.read ? 'read' : 'unread'}`}>
            <div className="title">{n.title}</div>
            <div className="body">{n.body}</div>
            <div className="time">{n.createdAt.toLocaleString()}</div>
          </div>
        ))}
      </div>
    </div>
  );
}
