import React, { useState } from 'react';

export default function ChatWindow() {
  const [text, setText] = useState('');
  const messages = [
    { text: 'Hello! ðŸ‘‹', from: 'them', createdAt: new Date() },
    { text: 'Hi, how are you?', from: 'me', createdAt: new Date() },
    { text: 'Iâ€™m good, thanks!', from: 'them', createdAt: new Date() }
  ];

  const submit = (e) => {
    e.preventDefault();
    if (!text.trim()) return;
    // just frontend â†’ no actual send
    setText('');
  };

  return (
    <div className="chat-window">
      <div className="messages">
        {messages.map((m, i) => (
          <div key={i} className={`message ${m.from}`}>
            <div className="text">{m.text}</div>
            <div className="time">{m.createdAt.toLocaleTimeString()}</div>
          </div>
        ))}
      </div>
      <form className="composer" onSubmit={submit}>
        <input
          value={text}
          onChange={e => setText(e.target.value)}
          placeholder="Type a message..."
        />
        <button type="submit">Send</button>
      </form>
      <h7>Press Enter to send, Shift+Enter for new line</h7>
      <hr/>
    </div>
  );
}
