import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { toggleNotif } from '../features/ui/uiSlice';
import { logout } from '../features/auth/authSlice';

export default function TopBar() {
  const dispatch = useDispatch();
  const user = useSelector(s => s.auth.user);
  const notifications = useSelector(s => s.ui.notifications || []);
  const unread = notifications.filter(n => !n.read).length;

  return (
    <div className="topbar">
      <div className="brand">MyChat</div>
      <div className="right">
        <div className="credits">Credits: <strong>{user?.credits ?? 0}</strong></div>
        <div className="notif" onClick={() => dispatch(toggleNotif())}>
          ðŸ””
          {unread > 0 && <span className="badge">{unread}</span>}
        </div>
        <button className="logout" onClick={() => dispatch(logout())}>Logout</button>
      </div>
    </div>
  );
}
