import React, { useState } from "react";
import "./ChatWindow.css";

export default function ChatWindow() {
  const [text, setText] = useState("");
  const [messages, setMessages] = useState([]); // start empty

  const submit = (e) => {
    e.preventDefault();
    if (!text.trim()) return;

    const newMessage = { text, from: "me", createdAt: new Date() };
    setMessages([...messages, newMessage]);
    setText("");
  };

  return (
    <div className="chat-window">
      {/* If no messages → show welcome screen */}
      {messages.length === 0 ? (
        <div className="welcome">
          <img
            src="https://cdn-icons-png.flaticon.com/512/4712/4712027.png"
            alt="chat logo"
            className="welcome-icon"
          />
          <h2>Welcome to AI Chat</h2>
          <p>
            Start a conversation with our AI assistant. Ask questions, get help
            with tasks, or explore ideas together.
          </p>
          <div className="examples">
            <button>✨ Explain quantum computing in simple terms</button>
            <button>📝 Write a Python function to sort a list</button>
            <button>🧘 What are the benefits of meditation?</button>
            <button>✈️ Help me plan a weekend trip to Paris</button>
          </div>
        </div>
      ) : (
        <div className="messages">
          {messages.map((m, i) => (
            <div key={i} className={`message ${m.from}`}>
              <div className="text">{m.text}</div>
              <div className="time">{m.createdAt.toLocaleTimeString()}</div>
            </div>
          ))}
        </div>
      )}

      <hr className="top-divider" />

      {/* Composer stays always visible */}
      <form className="composer" onSubmit={submit}>
        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Ask me anything..."
        />
        <button type="submit">Send</button>
      </form>

      <p className="hint">Press Enter to send, Shift+Enter for new line</p>
    </div>
  );
}
